import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../Footer';
import Menu from '../Menu';

export default function Payment() {

    const [Vnm, setVehi] = useState([]);
    const [Vname, setVehiNM] = useState('');
    const [email, seteDate] = useState('');
    const [amnt, setAmnt] = useState('');
    const [rmRk, setRmrk] = useState('');
    const [typ_e, setType] = useState('');
    const [msg, setmsg] = useState('');

    const getdata = () => {
        axios({
            method: 'get',
            url: 'http://localhost/fleet_manage/backend/Payment_c/VehicleName',
            responseType: 'json'
        }).then(function (response) {
            let VnmN = response.data.vehicl;
            setVehi(VnmN)
        });
    }

    useEffect(() => {
        getdata();
    }, []);

    const navigate = useNavigate();
    useEffect(() => {
        let token = localStorage.getItem('token')
        if (token == null) {
            navigate("/");
        }
    }, []);

    const save = () => {
        axios.post('http://localhost/fleet_manage/backend/Payment_c/saveVehi', {

            vehicle_id: Vname,
            trans_date: email,
            amount: amnt,
            remarks: rmRk,
            type: typ_e
        }, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': localStorage.getItem('token')
            }
        }).then(function (response) {
            let data = response.data;
            setmsg(data.msg);
            setVehiNM(data.msg);
            seteDate('');
            setRmrk('');
            setAmnt('');
            setType('');
            getPmnt();
        })
    }

    const [pmnts, setPmnts] = useState([]);
    const getPmnt = () => {
        axios({
            method: 'get',
            url: 'http://localhost/fleet_manage/backend/Payment_c/pmntGet',
            responseType: 'json'
        }).then(function (response) {            
            let pmnt = response.data.pmnts;
            setPmnts(pmnt);
           console.log(pmnt);
