import React, { useEffect, useState } from "react";
import Menu from "../Menu";
// import Menu from "../menu/Menu";
import Footer from "../Footer";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function Fuel_Edit() {
  const navigate = useNavigate();
  const p = useParams();
  const [vehicles, setvehicles] = useState([{ name :"", id: "" }]);
  const [drivers, setdrivers] = useState([{ name: "", id: "" }]);

  const [vehicle, setvehicle] = useState("");
  const [driver, setdriver] = useState("");
  const [date, setdate] = useState("");
  const [meter, setmeter] = useState("");
  const [qty, setqty] = useState("");
  const [amount, setamount] = useState("");
  const [remarks, setremarks] = useState("");
  const [msg, setmsg] = useState("");


  
  useEffect(() => {
    ///--------vehicle-----
    axios({
      method: "get",
      url: "http://localhost/fleet_manage/backend/Sharmin/getVehicle",
      responseType: "json",
    }).then(function (response) {
      setvehicles(response.data.vehicle);
    });
    //--------driver-(admin)--------
    axios({
      method: "get",
      url: "http://localhost/fleet_manage/backend/Sharmin/getDriver",
      responseType: "json",
    }).then(function (response) {
      setdrivers(response.data.driver);
    });
    // --------edit start-----------
    axios
        .post(
          "http://localhost/fleet_manage/backend/Sharmin/editfuel",
          {
            id: p.id,
          },
          {
            headers: {
              "Content-Type": "application/json",
              Authorization: localStorage.getItem("token"),
            },
          }
        )
       .then(function (response) {
      let data = response.data;
      console.log(data);
      setvehicle(data.fuel.vehicle_id);
      setdriver(data.fuel.driver_id);
      setdate(data.fuel.fill_date);
      setmeter(data.fuel.odometer);
      setamount(data.fuel.amount);
      setqty(data.fuel.qty);
      setremarks(data.fuel.remarks);
    });
   
    }, []);
        //---------amount= Quantity (*) fixamount--------
        const to = () => {
          if(qty==null){
             setamount(0)
          }else{
          // setamount(parseInt(qty) - parseInt(meter));
          setamount(parseInt(qty) * parseInt(2000));
          }
      
        };
      
    //------------update start--------
    const update = () => {
      axios
      .post(
        "http://localhost/fleet_manage/backend/Sharmin/updatefuel",
        {
          vehicle_id: vehicle,
          driver_id: driver,
          fill_date: date,
          qty: qty,
          odometer: meter,
          amount: amount,
          remarks: remarks,
          id: p.id,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: localStorage.getItem("token"),
          },
        }
      )
        .then(function (response) {
          let data = response.data;
          // console.log (data);
          setmsg(data.msg);
          setTimeout(() => navigate("/fuel_list"), 3000);
        });
    };

  return (
    <div className="hold-transition sidebar-mini">
      <div className="wrapper">
        <Menu />
        <div className="content-wrapper pb-2 mb-0">
          <div className="content-header">
            <div className="container-fluid">
              <div className="row mb-2">
                <div className="col-sm-6">
                  <h1 className="m-0 text-dark">Update_Fuel</h1>
                </div>
                <div className="col-sm-6">
                  <ol className="breadcrumb float-sm-right">
                    <li className="breadcrumb-item">
                      <a href="#">Fuel</a>
                    </li>
                    <li className="breadcrumb-item active">Update_Fuel</li>
                  </ol>
                </div>
                <h4 style={{color:'blue'}}>{msg}</h4>
              </div>
            </div>
          </div>

          <section className="content">
            <div className="container-fluid">
              <div className="card" >
                <div className="card-body">
                
                  <div className="row">
                    <div className="col-sm-6 col-md-3">
                      <label className="form-label">
                        Vechicle<span className="form-required">*</span>
                      </label>
                      <div className="form-group">
                        <select
                          className="form-control selectized form-required"
                          name="vehicle"
                          // required="true"
                          onChange={(e) => setvehicle(e.target.value)}
                        value={vehicle}>
                          <option value="">Select Vehicle</option>

                          {vehicles.map((d, i) => {
                            return (
                              <option value={d.id} key={i}>
                                {d.name}
                              </option>
                            );
                          })}
                        </select>
                      </div>
                    </div>
                    <div className="col-sm-6 col-md-3">
                      <div className="form-group">
                        <label className="form-label">
                          Added Driver
                          <span className="form-required">*</span>
                        </label>
                        <select
                          // required="true"
                          className="form-control selectized"
                          name="driver"
                          onChange={(e) => setdriver(e.target.value)}
                          value={driver} >
                          <option value="">Select Driver</option>
                          {drivers.map((di, i) => (
                            <option value={di.id} key={i}>
                              {di.name}
                            </option>
                          ))}
                        </select>
                      </div>
                    </div>
                    <div className="col-sm-6 col-md-3">
                      <div className="form-group">
                        <label className="form-label">
                          Fill Date
                          <span className="form-required">*</span>
                        </label>
                        <input
                          type="date"
                          // required="true"
                          className="form-control datepicker"
                          name="date"
                          placeholder="Fuel Fill Date"
                          onChange={(e) => setdate(e.target.value)}
                          value={date}
                        />
                      </div>
                    </div>
                    <div className="col-sm-6 col-md-3">
                      <div className="form-group">
                        <label className="form-label">
                          Quantity
                          <span className="form-required">*</span>
                        </label>
                        <input
                          type="text"
                          className="form-control"
                          name="qty"
                          placeholder="Quantity"
                          onChange={(e) => setqty(e.target.value)}
                          value={qty}
                        />
                      </div>
                    </div>
                    <div className="col-sm-6 col-md-3">
                      <div className="form-group">
                        <label className="form-label">
                          Odometer Reading
                          <span className="form-required">*</span>
                        </label>
                        <input
                          type="text"
                          className="form-control"
                          name="meter"
                          placeholder="Odometer Reading"
                          onChange={(e) => setmeter(e.target.value)}
                          value={meter}
                        />
                      </div>
                    </div>
                    <div className="col-sm-6 col-md-3">
                      <div className="form-group">
                        <label className="form-label">
                          Amount<span className="form-required">*</span>
                        </label>
                        <input
                          type="text"
                          className="form-control"
                          name="amount"
                          placeholder="Amount"
                          // onChange={(e) => setamount(e.target.value)}
                          onChange={to}
                          value={amount}
                        />
                      </div>
                    </div>
                    <div className="col-sm-6 col-md-6">
                      <div className="form-group">
                        <label className="form-label">Comment</label>
                        <input
                          type="text"
                          className="form-control"
                          name="remarks"
                          placeholder="Fuel Comments"
                          onChange={(e) => setremarks(e.target.value)}
                          value={remarks}
                        />
                      </div>
                    </div>
                    <br />
                    {/* <div className="col-sm-6 col-md-3">
                    <div className="form-group">
                     <label className="form-label">Need to add in expense?</label>
                         <input className="form-control form-check-input" id="exp" name="exp" type="checkbox"/>
                    </div>
                  </div> */}
                  </div>
                </div>
                {/* <input type="hidden" id="v_created_date" name="v_created_date" value="2023-03-28 06:02:47"/>
                 */}
                <div className="modal-footer">
                  <button
                    type="submit"
                   
                    onClick={update}
                    className="btn btn-primary"
                  >
                   Update Fuel
                  </button>
                </div>
              </div>
            </div>
          </section>
          <br/><br/><br/><br/><br/><br/><br/><br/><br/>
        
         
          <Footer />
        </div>
      </div>
    </div>
  );
}
